global.checkboxCheck=function (checkArray,key){

    if(checkArray != undefined && Array.isArray(checkArray) && checkArray.indexOf(key)>=0){

        return 'checked="checked"';
    }else if(checkArray != undefined && !Array.isArray(checkArray) &&  checkArray ==key){
        return 'checked="checked"';
    }else if(key == '-1' && checkArray != undefined && checkArray.length ==0){

        return 'checked="checked"';

    }else if(key == '-1' && checkArray == undefined ){

        return 'checked="checked"';
    }

}